mapper.select:
  
    SELECT t.id, t.name, t.address, t.account, t.bank, t.taxnumber, t.details, t.category, t.url, t.person, t.sex, t.age, t.email, t.mobile, t.tel, t.fax, t.qq, t.alww, t.msn, t.createtime, t.remark FROM t_tgks_wms_supplier t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.NAME LIKE CONCAT(CONCAT('%', #{name}),'%')
    
    
        AND t.ADDRESS LIKE CONCAT(CONCAT('%', #{address}),'%')
    
    
        AND t.ACCOUNT LIKE CONCAT(CONCAT('%', #{account}),'%')
    
    
        AND t.BANK = #{bank}
    
    
    	AND t.TAXNUMBER LIKE CONCAT(CONCAT('%', #{taxNumber}),'%')
    
    
        AND t.DETAILS = #{details}
    
    
        AND t.CATEGORY LIKE CONCAT(CONCAT('%', #{category}),'%')
    
    
        AND t.URL = #{url}
    
    
        AND t.PERSON LIKE CONCAT(CONCAT('%', #{person}),'%')
    
    
        AND t.MOBILE LIKE CONCAT(CONCAT('%', #{mobile}),'%')
    
    
    	AND t.TEL LIKE CONCAT(CONCAT('%', #{tel}),'%')
    
    
    	AND t.FAX LIKE CONCAT(CONCAT('%', #{fax}),'%')
    
    
        AND t.QQ = #{qq}
    
    
        AND t.ALWW = #{alww}
    
    
        AND t.MSN = #{msn}
    
    
        AND t.CREATETIME = #{createTime}
    
    
        AND t.REMARK = #{remark}
    


    ORDER BY ${sortSql}
